import React from 'react'
import { runInContext } from 'vm';

const seasonConfig = {
    summer:{
        text:"Let's hit the beach!",
        iconName:"sun"
    },
    winter:{
        text:"Burr is cold",
        iconName:"snowflake"
    },
    spring:{
        text:"flouring",
        iconName:"leaf"
    },
    autumn:{
        text:"raining",
        iconName:"rain"
    }

}

const getSeason=(lat,month)=>{
    // if(month>2&&month<9){
    //     return lat>0?'sumer':'winter';

    // }else{
    //     return lat>0?'winter':'summer';
    // }
    switch(month){
        case 1:
            return lat>0?'winter':'summer';
            break;
        case 2:
            return lat>0?'winter':'summer';
            break;
        case 3:
            return lat>0?'spring':'autumn';
            break;
        case 4:
            return lat>0?'spring':'autumn';
            break;
        case 5:
            return lat>0?'spring':'autumn';
            break;
        case 6:
            return lat>0?'summer':'winter';
            break;
        case 7:
            return lat>0?'summer':'winter';
            break;
        case 8:
            return lat>0?'summer':'winter';
            break;
        case 9:
            return lat>0?'autumn':'spring';
            break;
        case 10:
            return lat>0?'autumn':'spring';
            break;
        case 11:
            return lat>0?'autumn':'spring';
            break;

        case 12:
            return lat>0?'winter':'summer';
            break;
        default:
            console.log('default');
    }
}

const SeasonDisplay =props=>{
    const season = getSeason(props.lat,new Date().getMonth());

    const {text, iconName}=seasonConfig[season];
    return (
        <div>
            Latitude:{props.lats}
            Longitude:{props.langis}
            <p>{new Date().getMonth()}</p>
            <p>Season Display:{season}</p>
            <p>
                <h1> <i className={`${iconName} icon`}/> </h1>
                <h1>{text}</h1>
                <h1><i className={`${iconName} icon`} /> </h1>
            </p>
        </div>
    );
}

export default SeasonDisplay;
