import React, { Component } from 'react'
import { runInContext } from 'vm';
import './SeasondisplyStyle/seasonstyle.css';
import SearchApp from '../search/SearchApp';

const seasonConfig = {
    summer:{
        text:"Let's hit the beach!",
        iconName:"sun"
    },
    winter:{
        text:"Burr is cold",
        iconName:"snowflake"
    },
    spring:{
        text:"flouring",
        iconName:"leaf"
    },
    autumn:{
        text:"raining",
        iconName:"rain"
    }

}

const getSeason=(lat,month)=>{
    // if(month>2&&month<9){
    //     return lat>0?'sumer':'winter';

    // }else{
    //     return lat>0?'winter':'summer';
    // }
    switch(month){
        case 0:
            return lat>0?'winter':'summer';
            break;
        case 1:
            return lat>0?'winter':'summer';
            break;
        case 2:
            return lat>0?'spring':'autumn';
            break;
        case 3:
            return lat>0?'spring':'autumn';
            break;
        case 4:
            return lat>0?'spring':'autumn';
            break;
        case 5:
            return lat>0?'summer':'winter';
            break;
        case 6:
            return lat>0?'summer':'winter';
            break;
        case 7:
            return lat>0?'summer':'winter';
            break;
        case 8:
            return lat>0?'autumn':'spring';
            break;
        case 9:
            return lat>0?'autumn':'spring';
            break;
        case 10:
            return lat>0?'autumn':'spring';
            break;

        case 11:
            return lat>0?'winter':'summer';
            break;
        default:
            console.log('default');
    }
}





