import React,{Component} from 'react';


class Demo extends Component{
    constructor(props){
        super(props);
        this.state={
            status:true,
        }

    }

    hide_overlay() {
        alert('clicked');
        this.setState({
          status: false
        })
    }


    render(){
        return(
            <div>
                {/* <h1 onClick={this.hide_overlay()}>remove text</h1> */}
                {
                    this.state.status && <div onClick={this.hide_overlay()}>
                        demo text paragraph
                    </div>
                }
            </div>
        );
    }
}

export default Demo;
